import * as React from 'react';
import { IUser, OcurrenceRecord } from '../interfaces/IUser';
import api from './api'

  function GetApi(route: String){

    const [resp, setResp] = React.useState([]);

    React.useEffect(() => {
      api
      .get("/v1/" + route)
      .then((response) => ((setResp(response.data))))
      .catch((err) => {
        console.error("ops! ocorreu um erro" + err);
      });
    }, [''])

    return resp;

  } 
  const ocurrenceRecords: OcurrenceRecord[] = [
    {
      id: 1,
      accountId: 1,
      typeRecord: 1,
      amount: 300,
      createdAt: new Date()
    },
    {
      id: 1,
      accountId: 1,
      typeRecord: 2,
      amount: 100,
      createdAt: new Date()
    },
    {
      id: 1,
      accountId: 1,
      typeRecord: 1,
      amount: 500,
      createdAt: new Date()
    },
    {
      id: 1,
      accountId: 1,
      typeRecord: 1,
      amount: 200,
      createdAt: new Date()
    }
  ]

  const pessoa1 : IUser = {id: 1,
                          name: 'lucas',
                          phone: '123456789',
                          account: {id: 1 ,
                                    userId: 2,
                                    numberAccount: 12345,
                                    balance: 200, ocurrenceRecords: ocurrenceRecords,
                                    openingAt: new Date()
                                  }
                          }
  const pessoa2 : IUser = {id: 2,
                            name: 'daiane',
                            phone: '123456789',
                            account: {id: 1 ,
                                      userId: 2,
                                      numberAccount: 12345,
                                      balance: 200, ocurrenceRecords: ocurrenceRecords,
                                      openingAt: new Date()
                                    }
                            }


  const listPessoa: IUser[] = [];
  listPessoa.push(pessoa1, pessoa2);


  export function getUsers(){

    // return GetApi('Users');
    return listPessoa;
  }

  export function getUser(id : any){
    // return GetApi('Users/'+id);
    return listPessoa[id - 1];
  }

  export function getOcurrenceUser(id: any){
    return listPessoa[id - 1].account.ocurrenceRecords
  }

  export function deleteUser(e : IUser){
    api
    .delete("/v1/Users/" + e.id)
    .then(() => (console.log('Success')))
    .catch((err) => {
      console.error("ops! ocorreu um erro" + err);
    });
    document.location.reload();
  }
