import React, { MouseEventHandler, ReactElement, useEffect, useState } from "react";
import { IUser } from "./interfaces";
import './App.css'
import {getUsers, getUser} from './services/api_services'

//rotas
import {Routes, Route, Link} from 'react-router-dom'
import Home from "./templates/Home";
import Users from "./templates/Users";
import Sidebar from "./components/Siderbar/Sidebar";
import Header from "./components/Header/Header"
import { InfoUser } from "./templates/InfoUser";
import api from "./services/api";

export default function App() {
  const elementUsers : Array<ReactElement> = [];

  const [elements, setElements] = useState([]);

  function deleteUser(e : IUser){
    api
    .delete("/v1/Users/" + e.id)
    .then(() => (console.log('Success')))
    .catch((err) => {
      console.error("ops! ocorreu um erro" + err);
    });
    document.location.reload();
  }

    useEffect(() => {
      getUsers().forEach((e: IUser) => { 
        e.push(<tr className="table-infos">
                            <th scope="row">{e.id}</th>
                            <td>{e.name}</td>
                            <td>{e.phone}</td>
                            <td>{e.account.numberAccount}</td>
                            <div className='icons border-bottom'>
                              <span className="material-symbols-outlined col-3 text-primary option-icons">edit_note</span>
                              <Link to={'/info/'+e.id}><span className="material-symbols-outlined col-3 text-success option-icons">description</span></Link>
                              <span onClick={() => {deleteUser(e)}} className="material-symbols-outlined col-3 text-danger option-icons">delete</span>
                            </div>
                          </tr>
        ) 
      })
    })



  return (
    <div className="App">
        <Header/>
        <main>
          <Sidebar/>
          <Routes>
            <Route path="/" element={<Home/>}></Route>
            <Route path="/users" element={<Users dados={elementUsers}/>}></Route>
            <Route path='/info/:id' element={<InfoUser/>}></Route>
          </Routes>
        </main>
    </div>  
  );
}
