  return <div className='body'>
            <div className="card shadow-sm border-0 p-4">
                <div className="card-title text-start fs-5">
                    Criando Cliente
                </div>
                <hr />
                <div className="card-body">
                    <form>
                        <div className="form-group row align-items-center text-start">
                            <div className="col-2">
                                <label htmlFor="exampleInputEmail1">Nome:</label>
                            </div>
                            <div className="col-4">
                                <input onChange={e => e.target.value.trim().length > 0? setValName(true): setValName(false)} type="string" required className="form-control" id="inputName" aria-describedby="emailHelp" placeholder=""/>
                            </div>
                        </div>
                        <div className="form-group pt-3 row align-items-center text-start">
                            <div className="col-2">
                                <label htmlFor="exampleInputEmail1">Telefone:</label>
                            </div>
                            <div className="col-2">
                                <input onChange={e => e.target.value.trim().length > 10? setValPhone(true): setValPhone(false)} required type="tel"  className="form-control" id="inputTelefone"  placeholder="(00) 00000-0000"/>
                            </div>
                            <hr className='mt-4'/>
                        </div>
                        <div className="col-12 d-flex justify-content-end">
                            <button onClick={()=> {create}} className={`btn btn-secondary ${
                                (valName && valPhone)? 'able' : 'disabled'
                            }`} >Criar</button>
                        </div>
                        <div id="mensagem">Muito Obrigado por sua Mensagem!</div>

                    </form>
                </div>
            </div>
         </div>;
};

export default CreateUser;
