import { getValue } from '@testing-library/user-event/dist/utils';
import * as React from 'react';
import { useEffect, useState } from 'react';
import {Link, useNavigate} from 'react-router-dom';
import './CreateUser.css'

interface ICreateUserProps {
}

const CreateUser: React.FunctionComponent<ICreateUserProps> = (props) => {

    const [inpName, setInpName] = useState<HTMLElement| null>(document.getElementById('inputName'));
    const [teste, setTeste] = useState('blue')


  return <div className='body'>
            <div className="card shadow-sm border-0 p-4">
                <div className="card-title text-start fs-5">
                    Criando Cliente
                </div>
                <hr />
                <div className="card-body">
                    <form>
                        <div className="form-group row align-items-center text-start">
                            <div className="col-2">
                                <label htmlFor="exampleInputEmail1">Nome:</label>
                            </div>
                            <div className="col-4">
                                <input type="string" required className="form-control" id="inputName" aria-describedby="emailHelp" placeholder=""/>
                            </div>
                        </div>
                        <div className="form-group pt-3 row align-items-center text-start">
                            <div className="col-2">
                                <label htmlFor="exampleInputEmail1">Telefone:</label>
                            </div>
                            <div className="col-2">
                                <input required type="tel"  className="form-control" id="inputTelefone"  placeholder="(00) 00000-0000"/>
                            </div>
                            <hr className='mt-4'/>
                        </div>
                        <div className="col-12 d-flex justify-content-end">
                            <button className="btn btn-secondary" onClick={()=>{
                                    if(inpName == undefined){
                                        setTeste('red')
                                    }
                                    if(inpName.documen){
                                        setTeste('blue')
                                    }
                                }

                            }
                                style={{backgroundColor: teste}}>Criar</button>
                        </div>
                    </form>
                </div>
            </div>
         </div>;
};

export default CreateUser;
