import * as React from 'react';
import { useState } from 'react';
import icon_logo from '../../assets/img/icon_logo.png'
import bankpay_logo from '../../assets/img/bankpay_logo.png'
import "./Header.css";


export interface IHeaderProps {
}

export function Header (props: IHeaderProps) {

  const[style_sidebar, setStyle] = useState("header-sidebar");

  const changestyle = () => {
    setStyle('header-sidebar2')
  }

  return (
    <div className='header'>
        <header>
            <div className='header-sidebar' onClick={changestyle}>
              <img src={icon_logo} className='icon_logo' alt="" />
              <img src={bankpay_logo} className='bankpay_logo' alt="" />
            </div>
            <div className='header-main '>
              sidebar
            </div>
        </header>
    </div>
  );
}

const App = () => {
  const [style, setStyle] = useState("cont");

  const changeStyle = () => {
    console.log("you just clicked");

    setStyle("cont2");
  };
  return (
    <>
      <div className="App">CHANGE CSS STYLING WITH ONCLICK EVENT</div>
      <div className={style}>
        <button className="button" onClick={changeStyle}>
          Click me!
        </button>
      </div>
    </>
  );
};
export default App;
