            </div>
          </div>
          <hr />
          <h3>Movimentações</h3>
          <div className='row py-3'>
            <RowsInfo text={'Id'} data={user?.account}/>
            <RowsInfo text={'Id'} data={user?.account.id}/>
          </div>

        </div>

      </div>
    </div>
  );
}
