import { ReactElement, useEffect, useState } from 'react';
import { IUser, OcurrenceRecord } from '../interfaces/IUser';
import api from '../services/api';
import {getUser} from '../services/api_services';
import './InfoUser.css'

export interface IRowsInfo {
  datatime?: any;
  data?: any;
  text: String;
}

export function RowsInfo (props: IRowsInfo) {


  return (
    <div className='row justify-content-between'>
      <div className='col text-end'>
        <strong>{props.text}:</strong>
      </div>

      <div className='col text-start'>
        {props.data == null ? props.datatime :  props.data  }
      </div>
    </div>
  )
}



export interface IInfoUserProps {

}

export function InfoUser (props: IInfoUserProps) {

  const [user, setUser] = useState<IUser>();
  function userGet(){
    var url = window.location.href[window.location.href.length -1]
    api
        .get("/v1/Users/" + url)
        .then((response) => ((setUser(response.data))))
        .catch((err) => {
          console.error("ops! ocorreu um erro" + err);
        });
  }


  function OcurrenceGet(){

    const [ocurrences, setOcurrence] = useState([]);

    api
      .get("/v1/Accounts/" + user?.account.numberAccount+ "/OcurrencesRecord/Statement")
      .then((response) => ((setOcurrence(response.data))))
      .catch((err) => {
        console.error("ops! ocorreu um erro" + err);
    });

    return ocurrences;
  }

  const ocurrence : Array<ReactElement> = [];

  OcurrenceGet().forEach((o: OcurrenceRecord) => {
    ocurrence.push(
      <RowsInfo text={'id'} data={o.id}/>
    )
  })




  return (
    <div className='body'>
      <div className='card p-4'>
        <div className='card-title text-start fs-5'>
          Cliente: {user?.name}
        </div>
        <hr />
        <div className='card-body'>
          <h3>Informações</h3>
          <div className='row py-3'>
            <div className='col'>
              <div className='col-12 text-center d-flex justify-content-center'><h5>Pessoais</h5></div><hr/>  
                <RowsInfo text={'Id'} data={user?.id}/>
                <RowsInfo text={'Name'} data={user?.name}/>
                <RowsInfo text={'Telefone'} data={user?.phone}/>
            </div>
            <div className='col'>
              <div className='col-12 text-center d-flex justify-content-center'><h5>Conta</h5></div><hr/>  
                <RowsInfo text={'Id'} data={user?.account.id}/>
                <RowsInfo text={'User id'} data={user?.account.userId}/>
                <RowsInfo text={'Numero'} data={user?.account.numberAccount}/>
                <RowsInfo text={'Saldo'} data={user?.account.balance}/>
                <RowsInfo text={'Data abertura'} datatime={user?.account.openingAt}/>
            </div>
          </div>
          <hr />
          <h3>Movimentações</h3>
          <div className='row py-3'>
            <RowsInfo text={'Id'} data={ocurrence}/>
          </div>

        </div>

      </div>
    </div>
  );
}
