import { useEffect, useState } from 'react';
import { IUser } from '../interfaces/IUser';
import api from '../services/api';
import {getUser} from '../services/api_services';
import './InfoUser.css'

export interface IRowsInfo {
  datatime?: any;
  data?: any;
  text: String;
}

export function RowsInfo (props: IRowsInfo) {


  return (
    <div className='row justify-content-between'>
      <div className='col text-end'>
        <strong>{props.text}:</strong>
      </div>

      <div className='col text-start'>
        {props.data == null ? props.datatime :  props.data  }
      </div>
    </div>
  )
}



export interface IInfoUserProps {

}

export function InfoUser (props: IInfoUserProps) {

  function userGet(){
    const [userGet, setUser] = useState<IUser>();
    var url = window.location.href[window.location.href.length -1]
    api
        .get("/v1/Users/" + url)
        .then((response) => ((setUser(response.data))))
        .catch((err) => {
          console.error("ops! ocorreu um erro" + err);
        });
  }





  return (
    <div className='body'>
      <div className='card p-4'>
        <div className='card-title text-start fs-5'>
          Cliente: {userGet?.name}
        </div>
        <hr />
        <div className='card-body'>
          <h3>Informações</h3>
          <div className='row py-3'>
            <div className='col'>
              <div className='col-12 text-center d-flex justify-content-center'><h5>Pessoais</h5></div><hr/>  
                <RowsInfo text={'Id'} data={userGet?.id}/>
                <RowsInfo text={'Name'} data={userGet?.name}/>
                <RowsInfo text={'Telefone'} data={userGet?.phone}/>
            </div>
            <div className='col'>
              <div className='col-12 text-center d-flex justify-content-center'><h5>Conta</h5></div><hr/>  
                <RowsInfo text={'Id'} data={userGet?.account.id}/>
                <RowsInfo text={'userGet id'} data={userGet?.account.userId}/>
                <RowsInfo text={'Numero'} data={userGet?.account.numberAccount}/>
                <RowsInfo text={'Saldo'} data={userGet?.account.balance}/>
                <RowsInfo text={'Data abertura'} datatime={userGet?.account.openingAt}/>
            </div>
          </div>
          <hr />
          <h3>Movimentações</h3>
          <div className='row py-3'>
            <RowsInfo text={'Id'} data={userGet?.account}/>
            <RowsInfo text={'Id'} data={userGet?.account.id}/>
          </div>

        </div>

      </div>
    </div>
  );
}
