import * as React from 'react';
import { Link} from 'react-router-dom';
import './Card.css';

export interface ICardProps {
  link?: String;
  subtitle: String;
  color?: any;
  linearGradient? : Array<String>;
}

export default class Card extends React.Component<ICardProps> {

  public render() {

    const linearGradient: any = this.props.linearGradient;
    const color = this.props.color?? 'primary';
    const subtitle = this.props.subtitle?? 'Subtitle';
    const link = this.props.link ?? '/'; 

    return (
      <Link to={`${link}`} className='status-card card col p-0 m-3 bg-white'>
          <div className="card-body py-2 px-3 m-0">
            <div className='row'>
              <div className='col-8'>
                <div className="statusNumber text-start" style={{color: color}}>4</div>
                <div className="statusDesc text-muted text-start">{subtitle}</div>
              </div>   
              <span className="col icon material-symbols-outlined" style={{color: color}}>
                group
              </span>
            </div>
          </div>
            <div 
              className="card-footer d-flex justify-content-between text-white" 
              style={{backgroundImage: `linear-gradient(to right, ${linearGradient})`,
                      backgroundColor: color}}>
              Ver todos
              <span className="arrow material-symbols-outlined">arrow_forward</span>
            </div>
        </Link>
    );
  }
}
